import React, { useState } from 'react'
import Card from '../UI/Card'
import classes from "./AddUser.module.css";
import Button from './Button';


const AddUserForm = (props) => {

    const [userName, setUserName] = useState("")
    const [userAge, setUserAge] = useState("")

    
    const userNameHandler =(event)=>{
      
        setUserName(event.target.value);
    }
    const userAgeHandler =(event)=>{
        setUserAge(event.target.value);
    }
    const onFormSubmit =(event)=>{
       event.preventDefault();
       
       if(userName.trim().length === 0 || userAge.trim().length === 0){
        return console.log("Enter Both Values");
    }

    if(+userAge < 1 ){
        return console.log("Enter Age Above 1", userName);
    }
    const newObject ={
        name:userName,
        age:userAge,
        id: Math.random(),
    }

   props.sendToApp(newObject)
       setUserName("");
       setUserAge("");
    }


  return (
    <Card className={classes.input}>
        <form onSubmit={onFormSubmit}>
            <label htmlFor='username'>Username</label>
            <input  id="username" type="text"  onChange={userNameHandler} value={userName}/>
            <label htmlFor='age'>Age (Years)</label>
            <input id="age" type="number"  onChange={userAgeHandler} value={userAge}/>
            <Button  type='submit'>Add User</Button>
        </form>
    </Card>
  )
}

export default AddUserForm
